import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import express from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, requireAuth, requireRole } from "./auth";
import { setupWebSocket } from "./websocket";
import multer from "multer";
import path from "path";

// Configure multer for file uploads
const upload = multer({
  dest: 'uploads/',
  limits: {
    fileSize: 10 * 1024 * 1024 // 10MB limit
  }
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup authentication
  setupAuth(app);

  // Class routes
  app.get("/api/classes", async (req, res) => {
    try {
      const classes = await storage.getAllClasses();
      res.json(classes);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch classes" });
    }
  });

  app.get("/api/classes/:id", async (req, res) => {
    try {
      const classData = await storage.getClass(req.params.id);
      if (!classData) {
        return res.status(404).json({ message: "Class not found" });
      }
      res.json(classData);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch class" });
    }
  });

  app.post("/api/classes", requireAuth, requireRole('teacher'), async (req, res) => {
    try {
      const classData = {
        ...req.body,
        teacherId: req.user!.id,
        date: new Date(req.body.date)
      };
      const newClass = await storage.createClass(classData);
      res.status(201).json(newClass);
    } catch (error) {
      res.status(500).json({ message: "Failed to create class" });
    }
  });

  app.put("/api/classes/:id", requireAuth, requireRole('teacher'), async (req, res) => {
    try {
      const updatedClass = await storage.updateClass(req.params.id, req.body);
      if (!updatedClass) {
        return res.status(404).json({ message: "Class not found" });
      }
      res.json(updatedClass);
    } catch (error) {
      res.status(500).json({ message: "Failed to update class" });
    }
  });

  app.delete("/api/classes/:id", requireAuth, requireRole('teacher'), async (req, res) => {
    try {
      const deleted = await storage.deleteClass(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Class not found" });
      }
      res.sendStatus(204);
    } catch (error) {
      res.status(500).json({ message: "Failed to delete class" });
    }
  });

  // Enrollment routes
  app.post("/api/classes/:id/enroll", requireAuth, requireRole('student'), async (req, res) => {
    try {
      const studentId = req.user!.id;
      const classId = req.params.id;

      // Check if already enrolled
      const existingEnrollment = await storage.checkEnrollment(studentId, classId);
      if (existingEnrollment) {
        return res.status(400).json({ message: "Already enrolled in this class" });
      }

      const enrollment = await storage.enrollStudent({ studentId, classId });
      res.status(201).json(enrollment);
    } catch (error) {
      res.status(500).json({ message: "Failed to enroll in class" });
    }
  });

  app.get("/api/enrollments", requireAuth, async (req, res) => {
    try {
      const userId = req.user!.id;
      const enrollments = await storage.getEnrollmentsByStudent(userId);
      res.json(enrollments);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch enrollments" });
    }
  });

  // Content routes
  app.get("/api/classes/:id/content", requireAuth, async (req, res) => {
    try {
      const content = await storage.getContentByClass(req.params.id);
      res.json(content);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch content" });
    }
  });

  app.post("/api/classes/:id/content", requireAuth, requireRole('teacher'), upload.single('file'), async (req, res) => {
    try {
      const { title, type, content: textContent } = req.body;
      const classId = req.params.id;

      const contentData = {
        classId,
        title,
        type,
        fileUrl: req.file ? `/uploads/${req.file.filename}` : null,
        content: textContent || null
      };

      const newContent = await storage.createContent(contentData);
      res.status(201).json(newContent);
    } catch (error) {
      res.status(500).json({ message: "Failed to create content" });
    }
  });

  // Chat routes
  app.get("/api/classes/:id/messages", requireAuth, async (req, res) => {
    try {
      const messages = await storage.getMessagesByClass(req.params.id);
      res.json(messages);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch messages" });
    }
  });

  // Rating routes
  app.post("/api/ratings", requireAuth, requireRole('student'), async (req, res) => {
    try {
      const { teacherId, classId, rating, comment } = req.body;
      const studentId = req.user!.id;

      const newRating = await storage.createRating({
        studentId,
        teacherId,
        classId,
        rating,
        comment
      });

      res.status(201).json(newRating);
    } catch (error) {
      res.status(500).json({ message: "Failed to create rating" });
    }
  });

  app.get("/api/teachers/:id/ratings", async (req, res) => {
    try {
      const ratings = await storage.getRatingsByTeacher(req.params.id);
      const averageRating = await storage.getAverageRating(req.params.id);
      res.json({ ratings, averageRating });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch ratings" });
    }
  });

  // Statistics routes
  app.get("/api/stats", requireAuth, requireRole('admin'), async (req, res) => {
    try {
      const userStats = await storage.getUserStats();
      const classStats = await storage.getClassStats();
      res.json({ ...userStats, ...classStats });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch statistics" });
    }
  });

  // Teacher-specific routes
  app.get("/api/teacher/classes", requireAuth, requireRole('teacher'), async (req, res) => {
    try {
      const classes = await storage.getClassesByTeacher(req.user!.id);
      res.json(classes);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch teacher classes" });
    }
  });

  // Serve uploaded files
  app.use('/uploads', express.static(path.join(process.cwd(), 'uploads')));

  const httpServer = createServer(app);

  // Setup WebSocket
  setupWebSocket(httpServer);

  return httpServer;
}

server/storage.ts.content:
import { 
  users, 
  classes, 
  enrollments, 
  content, 
  messages, 
  ratings,
  type User, 
  type InsertUser,
  type Class,
  type InsertClass,
  type Enrollment,
  type InsertEnrollment,
  type Content,
  type InsertContent,
  type Message,
  type InsertMessage,
  type Rating,
  type InsertRating
} from "@shared/schema";
import { db, pool } from "./db";
import { eq, desc, count, avg, and } from "drizzle-orm";
import session from "express-session";
import connectPg from "connect-pg-simple";

const PostgresSessionStore = connectPg(session);

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Class operations
  getClass(id: string): Promise<Class | undefined>;
  getClassesByTeacher(teacherId: string): Promise<Class[]>;
  getAllClasses(): Promise<Class[]>;
  createClass(classData: InsertClass): Promise<Class>;
  updateClass(id: string, updates: Partial<InsertClass>): Promise<Class | undefined>;
  deleteClass(id: string): Promise<boolean>;

  // Enrollment operations
  enrollStudent(enrollment: InsertEnrollment): Promise<Enrollment>;
  getEnrollmentsByStudent(studentId: string): Promise<Enrollment[]>;
  getEnrollmentsByClass(classId: string): Promise<Enrollment[]>;
  checkEnrollment(studentId: string, classId: string): Promise<Enrollment | undefined>;

  // Content operations
  getContentByClass(classId: string): Promise<Content[]>;
  createContent(contentData: InsertContent): Promise<Content>;

  // Message operations
  getMessagesByClass(classId: string): Promise<Message[]>;
  createMessage(messageData: InsertMessage): Promise<Message>;

  // Rating operations
  createRating(ratingData: InsertRating): Promise<Rating>;
  getRatingsByTeacher(teacherId: string): Promise<Rating[]>;
  getAverageRating(teacherId: string): Promise<number>;

  // Statistics
  getUserStats(): Promise<{ totalUsers: number; totalStudents: number; totalTeachers: number }>;
  getClassStats(): Promise<{ totalClasses: number; liveClasses: number }>;

  sessionStore: connectPg.PGStore;
}

export class DatabaseStorage implements IStorage {
  sessionStore: connectPg.PGStore;

  constructor() {
    this.sessionStore = new PostgresSessionStore({ 
      pool, 
      createTableIfMissing: true 
    });
  }

  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async createUser(userData: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(userData).returning();
    return user;
  }

  async getClass(id: string): Promise<Class | undefined> {
    const [classData] = await db.select().from(classes).where(eq(classes.id, id));
    return classData;
  }

  async getClassesByTeacher(teacherId: string): Promise<Class[]> {
    return await db.select().from(classes).where(eq(classes.teacherId, teacherId)).orderBy(desc(classes.createdAt));
  }

  async getAllClasses(): Promise<Class[]> {
    return await db.select().from(classes).orderBy(desc(classes.createdAt));
  }

  async createClass(classData: InsertClass): Promise<Class> {
    const [newClass] = await db.insert(classes).values(classData).returning();
    return newClass;
  }

  async updateClass(id: string, updates: Partial<InsertClass>): Promise<Class | undefined> {
    const [updatedClass] = await db.update(classes)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(classes.id, id))
      .returning();
    return updatedClass;
  }

  async deleteClass(id: string): Promise<boolean> {
    const result = await db.delete(classes).where(eq(classes.id, id));
    return (result.rowCount ?? 0) > 0;
  }

  async enrollStudent(enrollment: InsertEnrollment): Promise<Enrollment> {
    const [newEnrollment] = await db.insert(enrollments).values(enrollment).returning();
    return newEnrollment;
  }

  async getEnrollmentsByStudent(studentId: string): Promise<Enrollment[]> {
    return await db.select().from(enrollments).where(eq(enrollments.studentId, studentId));
  }

  async getEnrollmentsByClass(classId: string): Promise<Enrollment[]> {
    return await db.select().from(enrollments).where(eq(enrollments.classId, classId));
  }

  async checkEnrollment(studentId: string, classId: string): Promise<Enrollment | undefined> {
    const [enrollment] = await db.select().from(enrollments)
      .where(and(eq(enrollments.studentId, studentId), eq(enrollments.classId, classId)));
    return enrollment;
  }

  async getContentByClass(classId: string): Promise<Content[]> {
    return await db.select().from(content).where(eq(content.classId, classId)).orderBy(desc(content.createdAt));
  }

  async createContent(contentData: InsertContent): Promise<Content> {
    const [newContent] = await db.insert(content).values(contentData).returning();
    return newContent;
  }

  async getMessagesByClass(classId: string): Promise<Message[]> {
    return await db.select().from(messages).where(eq(messages.classId, classId)).orderBy(desc(messages.timestamp));
  }

  async createMessage(messageData: InsertMessage): Promise<Message> {
    const [newMessage] = await db.insert(messages).values(messageData).returning();
    return newMessage;
  }

  async createRating(ratingData: InsertRating): Promise<Rating> {
    const [newRating] = await db.insert(ratings).values(ratingData).returning();
    return newRating;
  }

  async getRatingsByTeacher(teacherId: string): Promise<Rating[]> {
    return await db.select().from(ratings).where(eq(ratings.teacherId, teacherId)).orderBy(desc(ratings.createdAt));
  }

  async getAverageRating(teacherId: string): Promise<number> {
    const [result] = await db.select({ avg: avg(ratings.rating) })
      .from(ratings)
      .where(eq(ratings.teacherId, teacherId));
    return Number(result.avg) || 0;
  }

  async getUserStats(): Promise<{ totalUsers: number; totalStudents: number; totalTeachers: number }> {
    const [totalUsers] = await db.select({ count: count() }).from(users);
    const [totalStudents] = await db.select({ count: count() }).from(users).where(eq(users.role, 'student'));
    const [totalTeachers] = await db.select({ count: count() }).from(users).where(eq(users.role, 'teacher'));

    return {
      totalUsers: totalUsers.count,
      totalStudents: totalStudents.count,
      totalTeachers: totalTeachers.count
    };
  }

  async getClassStats(): Promise<{ totalClasses: number; liveClasses: number }> {
    const [totalClasses] = await db.select({ count: count() }).from(classes);
    const [liveClasses] = await db.select({ count: count() }).from(classes).where(eq(classes.isLive, true));

    return {
      totalClasses: totalClasses.count,
      liveClasses: liveClasses.count
    };
  }
}

export const storage = new DatabaseStorage();

