import { TeacherSidebar } from "@/components/teacher-sidebar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { apiRequest } from "@/lib/queryClient";
import { insertClassSchema, type InsertClass } from "@shared/schema";
import { CalendarDays, Clock, Users, BookOpen, ArrowLeft } from "lucide-react";
import { Link } from "wouter";
import { z } from "zod";

const createClassSchema = insertClassSchema.extend({
  dateString: z.string().min(1, "Date is required"),
  timeString: z.string().min(1, "Time is required"),
}).omit({ teacherId: true, date: true });

type CreateClassForm = z.infer<typeof createClassSchema>;

export default function CreateClass() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [, setLocation] = useLocation();

  const form = useForm<CreateClassForm>({
    resolver: zodResolver(createClassSchema),
    defaultValues: {
      title: "",
      description: "",
      subject: "",
      price: "0",
      maxStudents: 20,
      duration: 60,
      dateString: "",
      timeString: "",
      isLive: false,
    },
  });

  const createClassMutation = useMutation({
    mutationFn: async (data: CreateClassForm) => {
      const { dateString, timeString, ...classData } = data;
      const datetime = new Date(`${dateString}T${timeString}`);

      return apiRequest("POST", "/api/classes", {
        ...classData,
        date: datetime.toISOString(),
      });
    },
    onSuccess: () => {
      toast({
        title: "Success!",
        description: "Class created successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/teacher/classes"] });
      setLocation("/teacher");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create class",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: CreateClassForm) => {
    createClassMutation.mutate(data);
  };

  return (
    <div className="flex min-h-screen pt-16">
      <TeacherSidebar />

      <div className="flex-1 ml-64 p-6">
        <div className="mb-8">
          <Link href="/teacher">
            <Button variant="ghost" className="mb-4" data-testid="button-back">
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Dashboard
            </Button>
          </Link>
          <h1 className="text-3xl font-bold mb-2">Create New Class</h1>
          <p className="text-muted-foreground">Schedule a new live class for your students</p>
        </div>

        <Card className="glass max-w-2xl">
          <CardHeader>
            <CardTitle className="flex items-center">
              <BookOpen className="w-5 h-5 mr-2" />
              Class Details
            </CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                <FormField
                  control={form.control}
                  name="title"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Class Title</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="e.g., Advanced React Patterns" 
                          {...field} 
                          data-testid="input-title"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea 
                          placeholder="Describe what students will learn in this class..."
                          rows={3}
                          {...field}
                          value={field.value || ""}
                          data-testid="input-description"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="grid md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="subject"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Subject</FormLabel>
                        <Select onValueChange={field.onChange} value={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-subject">
                              <SelectValue placeholder="Select subject" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="programming">Programming</SelectItem>
                            <SelectItem value="design">Design</SelectItem>
                            <SelectItem value="business">Business</SelectItem>
                            <SelectItem value="marketing">Marketing</SelectItem>
                            <SelectItem value="data-science">Data Science</SelectItem>
                            <SelectItem value="other">Other</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="price"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Price ($)</FormLabel>
                        <FormControl>
                          <Input 
                            type="text" 
                            placeholder="0" 
                            {...field}
                            data-testid="input-price"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="grid md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="maxStudents"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center">
                          <Users className="w-4 h-4 mr-2" />
                          Max Students
                        </FormLabel>
                        <FormControl>
                          <Input 
                            type="number" 
                            placeholder="20" 
                            {...field}
                            onChange={(e) => field.onChange(Number(e.target.value))}
                            data-testid="input-max-students"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="duration"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center">
                          <Clock className="w-4 h-4 mr-2" />
                          Duration (minutes)
                        </FormLabel>
                        <FormControl>
                          <Input 
                            type="number" 
                            placeholder="60" 
                            {...field}
                            onChange={(e) => field.onChange(Number(e.target.value))}
                            data-testid="input-duration"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="grid md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="dateString"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center">
                          <CalendarDays className="w-4 h-4 mr-2" />
                          Date
                        </FormLabel>
                        <FormControl>
                          <Input 
                            type="date" 
                            {...field}
                            data-testid="input-date"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="timeString"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center">
                          <Clock className="w-4 h-4 mr-2" />
                          Time
                        </FormLabel>
                        <FormControl>
                          <Input 
                            type="time" 
                            {...field}
                            data-testid="input-time"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <div className="flex gap-4 pt-4">
                  <Button 
                    type="submit" 
                    className="bg-blue-500 hover:bg-blue-600" 
                    disabled={createClassMutation.isPending}
                    data-testid="button-create-class"
                  >
                    {createClassMutation.isPending ? "Creating..." : "Create Class"}
                  </Button>
                  <Link href="/teacher">
                    <Button variant="outline" data-testid="button-cancel">
                      Cancel
                    </Button>
                  </Link>
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/admin-classes.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Search, CalendarDays, Clock, Users, Eye, Ban, CheckCircle } from "lucide-react";
import { AdminSidebar } from "@/components/admin-sidebar";
import { Class, User } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { format } from "date-fns";

interface ClassWithTeacher extends Class {
  teacher: User;
  enrollmentCount: number;
}

export function AdminClasses() {
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState("");
  const { toast } = useToast();

  const { data: classes, isLoading } = useQuery<ClassWithTeacher[]>({
    queryKey: ["/api/admin/classes"],
  });

  const updateClassMutation = useMutation({
    mutationFn: async ({ classId, updates }: { classId: string; updates: Partial<Class> }) => {
      return apiRequest("PATCH", `/api/admin/classes/${classId}`, updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/classes"] });
      toast({
        title: "Success",
        description: "Class updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update class",
        variant: "destructive",
      });
    },
  });

  const filteredClasses = classes?.filter(classItem => {
    const matchesSearch = classItem.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      classItem.description?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      classItem.teacher.firstName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      classItem.teacher.lastName.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus = !statusFilter || 
      (statusFilter === 'live' && classItem.isLive) ||
      (statusFilter === 'scheduled' && !classItem.isLive && new Date(classItem.date) > new Date()) ||
      (statusFilter === 'completed' && !classItem.isLive && new Date(classItem.date) <= new Date());

    return matchesSearch && matchesStatus;
  });

  const getStatusBadge = (classItem: Class) => {
    if (classItem.isLive) {
      return <Badge className="bg-red-500 hover:bg-red-600">Live</Badge>;
    } else if (new Date(classItem.date) > new Date()) {
      return <Badge variant="secondary">Scheduled</Badge>;
    } else {
      return <Badge variant="outline">Completed</Badge>;
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <AdminSidebar />
        <main className="pl-64 p-8">
          <div className="max-w-7xl mx-auto">
            <div className="text-center">Loading classes...</div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <AdminSidebar />
      <main className="pl-64 p-8">
        <div className="max-w-7xl mx-auto">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Class Management</h1>
            <p className="text-gray-600 dark:text-gray-300 mt-2">Monitor and manage all classes on the platform</p>
          </div>

          {/* Filters */}
          <div className="flex gap-4 mb-6">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                placeholder="Search classes or teachers..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search"
              />
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-40" data-testid="select-status-filter">
                <SelectValue placeholder="All status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All status</SelectItem>
                <SelectItem value="live">Live</SelectItem>
                <SelectItem value="scheduled">Scheduled</SelectItem>
                <SelectItem value="completed">Completed</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Statistics */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Total Classes</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-3xl font-bold" data-testid="stat-total-classes">
                  {classes?.length || 0}
                </div>
              </CardContent>
            </Card>
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Live Classes</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-3xl font-bold text-red-600" data-testid="stat-live-classes">
                  {classes?.filter(c => c.isLive).length || 0}
                </div>
              </CardContent>
            </Card>
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Scheduled</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-3xl font-bold text-blue-600" data-testid="stat-scheduled-classes">
                  {classes?.filter(c => !c.isLive && new Date(c.date) > new Date()).length || 0}
                </div>
              </CardContent>
            </Card>
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Total Enrollments</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-3xl font-bold text-green-600" data-testid="stat-total-enrollments">
                  {classes?.reduce((sum, c) => sum + c.enrollmentCount, 0) || 0}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Classes List */}
          {!filteredClasses || filteredClasses.length === 0 ? (
            <Card>
              <CardContent className="text-center py-12">
                <h3 className="text-xl font-semibold mb-2" data-testid="text-no-classes">
                  {searchQuery || statusFilter ? "No classes found" : "No classes yet"}
                </h3>
                <p className="text-gray-600 dark:text-gray-300">
                  {searchQuery || statusFilter 
                    ? "Try adjusting your search or filters"
                    : "Classes will appear here as teachers create them"
                  }
                </p>
              </CardContent>
            </Card>
          ) : (
            <div className="grid gap-6">
              {filteredClasses.map((classItem) => (
                <Card key={classItem.id} className="hover:shadow-md transition-shadow">
                  <CardHeader>
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          {getStatusBadge(classItem)}
                          <Badge variant="outline" data-testid={`badge-subject-${classItem.id}`}>
                            {classItem.subject}
                          </Badge>
                        </div>
                        <CardTitle className="text-xl mb-2" data-testid={`text-class-title-${classItem.id}`}>
                          {classItem.title}
                        </CardTitle>
                        <CardDescription data-testid={`text-class-description-${classItem.id}`}>
                          {classItem.description}
                        </CardDescription>
                      </div>
                    </div>
                  </CardHeader>
                  <CardContent>
                    {/* Teacher Info */}
                    <div className="flex items-center gap-3 mb-4">
                      <Avatar className="h-8 w-8">
                        <AvatarFallback data-testid={`avatar-teacher-${classItem.id}`}>
                          {classItem.teacher.firstName[0]}{classItem.teacher.lastName[0]}
                        </AvatarFallback>
                      </Avatar>
                      <div>
                        <p className="font-medium text-sm" data-testid={`text-teacher-name-${classItem.id}`}>
                          {classItem.teacher.firstName} {classItem.teacher.lastName}
                        </p>
                        <p className="text-xs text-gray-500 dark:text-gray-400" data-testid={`text-teacher-email-${classItem.id}`}>
                          {classItem.teacher.email}
                        </p>
                      </div>
                    </div>

                    {/* Class Details */}
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
                      <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                        <CalendarDays className="w-4 h-4 mr-2" />
                        <span data-testid={`text-class-date-${classItem.id}`}>
                          {format(new Date(classItem.date), 'MMM dd, yyyy')}
                        </span>
                      </div>
                      <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                        <Clock className="w-4 h-4 mr-2" />
                        <span data-testid={`text-class-duration-${classItem.id}`}>
                          {classItem.duration} mins
                        </span>
                      </div>
                      <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                        <Users className="w-4 h-4 mr-2" />
                        <span data-testid={`text-class-enrollment-${classItem.id}`}>
                          {classItem.enrollmentCount}/{classItem.maxStudents} enrolled
                        </span>
                      </div>
                      <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                        <span className="text-lg font-semibold text-green-600" data-testid={`text-class-price-${classItem.id}`}>
                          ${classItem.price}
                        </span>
                      </div>
                    </div>

                    <div className="flex gap-2">
                      <Button variant="outline" size="sm" data-testid={`button-view-${classItem.id}`}>
                        <Eye className="w-4 h-4 mr-2" />
                        View Details
                      </Button>
                      {classItem.isLive && (
                        <Button variant="outline" size="sm" data-testid={`button-monitor-${classItem.id}`}>
                          Monitor Live Class
                        </Button>
                      )}
                      <Button 
                        variant="outline" 
                        size="sm" 
                        className="ml-auto text-red-600 hover:text-red-700" 
                        data-testid={`button-suspend-${classItem.id}`}
                      >
                        <Ban className="w-4 h-4 mr-2" />
                        Suspend
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

client/src/pages/admin-payments.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Search, DollarSign, CreditCard, TrendingUp, RefreshCw, Download } from "lucide-react";
import { AdminSidebar } from "@/components/admin-sidebar";
import { format } from "date-fns";

interface Payment {
  id: string;
  amount: number;
  status: 'completed' | 'pending' | 'failed' | 'refunded';
  paymentMethod: string;
  transactionId: string;
  createdAt: string;
  student: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
  };
  class: {
    id: string;
    title: string;
    teacher: {
      firstName: string;
      lastName: string;
    };
  };
}

interface PaymentStats {
  totalRevenue: number;
  totalTransactions: number;
  successRate: number;
  averageOrderValue: number;
  monthlyGrowth: number;
}

export function AdminPayments() {
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState("");
  const [dateRange, setDateRange] = useState("");

  const { data: payments, isLoading } = useQuery<Payment[]>({
    queryKey: ["/api/admin/payments"],
  });

  const { data: stats } = useQuery<PaymentStats>({
    queryKey: ["/api/admin/payment-stats"],
  });

  const filteredPayments = payments?.filter(payment => {
    const matchesSearch = payment.student.firstName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      payment.student.lastName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      payment.student.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
      payment.class.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      payment.transactionId.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus = !statusFilter || payment.status === statusFilter;

    return matchesSearch && matchesStatus;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300';
      case 'pending': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300';
      case 'failed': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300';
      case 'refunded': return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-300';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-300';
    }
  };

  const mockStats: PaymentStats = stats || {
    totalRevenue: 0,
    totalTransactions: 0,
    successRate: 0,
    averageOrderValue: 0,
    monthlyGrowth: 0
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <AdminSidebar />
        <main className="pl-64 p-8">
          <div className="max-w-7xl mx-auto">
            <div className="text-center">Loading payment data...</div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <AdminSidebar />
      <main className="pl-64 p-8">
        <div className="max-w-7xl mx-auto">
          <div className="mb-8">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Payment Management</h1>
                <p className="text-gray-600 dark:text-gray-300 mt-2">Monitor transactions and revenue analytics</p>
              </div>
              <div className="flex gap-2">
                <Button variant="outline" data-testid="button-refresh">
                  <RefreshCw className="w-4 h-4 mr-2" />
                  Refresh
                </Button>
                <Button variant="outline" data-testid="button-export">
                  <Download className="w-4 h-4 mr-2" />
                  Export
                </Button>
              </div>
            </div>
          </div>

          {/* Payment Statistics */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6 mb-8">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Revenue</CardTitle>
                <DollarSign className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="stat-total-revenue">
                  ${mockStats.totalRevenue.toFixed(2)}
                </div>
                <p className="text-xs text-muted-foreground">All time earnings</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Transactions</CardTitle>
                <CreditCard className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="stat-total-transactions">
                  {mockStats.totalTransactions}
                </div>
                <p className="text-xs text-muted-foreground">Total payments</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Success Rate</CardTitle>
                <TrendingUp className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="stat-success-rate">
                  {mockStats.successRate.toFixed(1)}%
                </div>
                <p className="text-xs text-muted-foreground">Payment success</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Avg Order Value</CardTitle>
                <DollarSign className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="stat-avg-order">
                  ${mockStats.averageOrderValue.toFixed(2)}
                </div>
                <p className="text-xs text-muted-foreground">Per transaction</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Monthly Growth</CardTitle>
                <TrendingUp className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="stat-monthly-growth">
                  {mockStats.monthlyGrowth > 0 ? '+' : ''}{mockStats.monthlyGrowth.toFixed(1)}%
                </div>
                <p className="text-xs text-muted-foreground">vs last month</p>
              </CardContent>
            </Card>
          </div>

          {/* Filters */}
          <div className="flex gap-4 mb-6">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                placeholder="Search by student, class, or transaction ID..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search"
              />
            </div>
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-40" data-testid="select-status-filter">
                <SelectValue placeholder="All status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All status</SelectItem>
                <SelectItem value="completed">Completed</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
                <SelectItem value="failed">Failed</SelectItem>
                <SelectItem value="refunded">Refunded</SelectItem>
              </SelectContent>
            </Select>
            <Select value={dateRange} onValueChange={setDateRange}>
              <SelectTrigger className="w-40" data-testid="select-date-filter">
                <SelectValue placeholder="Date range" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All time</SelectItem>
                <SelectItem value="today">Today</SelectItem>
                <SelectItem value="week">This week</SelectItem>
                <SelectItem value="month">This month</SelectItem>
                <SelectItem value="quarter">This quarter</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Payments List */}
          {!filteredPayments || filteredPayments.length === 0 ? (
            <Card>
              <CardContent className="text-center py-12">
                <h3 className="text-xl font-semibold mb-2" data-testid="text-no-payments">
                  {searchQuery || statusFilter ? "No payments found" : "No payments yet"}
                </h3>
                <p className="text-gray-600 dark:text-gray-300">
                  {searchQuery || statusFilter 
                    ? "Try adjusting your search or filters"
                    : "Payments will appear here as students enroll in classes"
                  }
                </p>
              </CardContent>
            </Card>
          ) : (
            <Card>
              <CardHeader>
                <CardTitle>Payment Transactions ({filteredPayments.length})</CardTitle>
                <CardDescription>Recent payment activity and transaction details</CardDescription>
              </CardHeader>
              <CardContent className="p-0">
                <div className="space-y-0 divide-y">
                  {filteredPayments.map((payment) => (
                    <div key={payment.id} className="p-6 flex items-center justify-between">
                      <div className="flex items-center gap-4 flex-1">
                        <Avatar className="h-10 w-10">
                          <AvatarFallback data-testid={`avatar-student-${payment.id}`}>
                            {payment.student.firstName[0]}{payment.student.lastName[0]}
                          </AvatarFallback>
                        </Avatar>

                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <h3 className="font-semibold" data-testid={`text-student-name-${payment.id}`}>
                              {payment.student.firstName} {payment.student.lastName}
                            </h3>
                            <Badge className={getStatusColor(payment.status)} data-testid={`badge-status-${payment.id}`}>
                              {payment.status.charAt(0).toUpperCase() + payment.status.slice(1)}
                            </Badge>
                          </div>
                          <p className="text-sm text-gray-600 dark:text-gray-300 mb-1" data-testid={`text-class-title-${payment.id}`}>
                            {payment.class.title}
                          </p>
                          <div className="flex items-center gap-4 text-xs text-gray-500 dark:text-gray-400">
                            <span data-testid={`text-transaction-id-${payment.id}`}>
                              ID: {payment.transactionId}
                            </span>
                            <span data-testid={`text-payment-method-${payment.id}`}>
                              {payment.paymentMethod}
                            </span>
                            <span data-testid={`text-payment-date-${payment.id}`}>
                              {format(new Date(payment.createdAt), 'MMM dd, yyyy HH:mm')}
                            </span>
                          </div>
                        </div>

                        <div className="text-right">
                          <p className="text-xl font-bold" data-testid={`text-payment-amount-${payment.id}`}>
                            ${payment.amount.toFixed(2)}
                          </p>
                          <p className="text-sm text-gray-500 dark:text-gray-400" data-testid={`text-teacher-name-${payment.id}`}>
                            to {payment.class.teacher.firstName} {payment.class.teacher.lastName}
                          </p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      </main>
    </div>
  );
}

