import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { BarChart3, TrendingUp, Users, BookOpen, DollarSign, Calendar, Download, RefreshCw } from "lucide-react";
import { AdminSidebar } from "@/components/admin-sidebar";
import { useState } from "react";

interface PlatformReports {
  overview: {
    totalUsers: number;
    totalClasses: number;
    totalRevenue: number;
    activeTeachers: number;
    completionRate: number;
    userGrowth: number;
    revenueGrowth: number;
  };
  userEngagement: {
    dailyActiveUsers: number;
    averageSessionDuration: number;
    classCompletionRate: number;
    repeatEnrollmentRate: number;
  };
  teacherPerformance: Array<{
    teacherId: string;
    teacherName: string;
    totalClasses: number;
    totalStudents: number;
    averageRating: number;
    totalRevenue: number;
  }>;
  popularClasses: Array<{
    classId: string;
    title: string;
    enrollmentCount: number;
    completionRate: number;
    averageRating: number;
    revenue: number;
  }>;
  revenueBreakdown: {
    bySubject: Array<{
      subject: string;
      revenue: number;
      percentage: number;
    }>;
    byMonth: Array<{
      month: string;
      revenue: number;
      growth: number;
    }>;
  };
}

export function AdminReports() {
  const [reportPeriod, setReportPeriod] = useState("month");
  const [reportType, setReportType] = useState("overview");

  const { data: reports, isLoading } = useQuery<PlatformReports>({
    queryKey: ["/api/admin/reports", reportPeriod],
  });

  const mockReports: PlatformReports = reports || {
    overview: {
      totalUsers: 0,
      totalClasses: 0,
      totalRevenue: 0,
      activeTeachers: 0,
      completionRate: 0,
      userGrowth: 0,
      revenueGrowth: 0
    },
    userEngagement: {
      dailyActiveUsers: 0,
      averageSessionDuration: 0,
      classCompletionRate: 0,
      repeatEnrollmentRate: 0
    },
    teacherPerformance: [],
    popularClasses: [],
    revenueBreakdown: {
      bySubject: [],
      byMonth: []
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <AdminSidebar />
        <main className="pl-64 p-8">
          <div className="max-w-7xl mx-auto">
            <div className="text-center">Loading reports...</div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <AdminSidebar />
      <main className="pl-64 p-8">
        <div className="max-w-7xl mx-auto">
          <div className="mb-8">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Platform Reports</h1>
                <p className="text-gray-600 dark:text-gray-300 mt-2">Analytics and insights for platform performance</p>
              </div>
              <div className="flex gap-2">
                <Select value={reportPeriod} onValueChange={setReportPeriod}>
                  <SelectTrigger className="w-32" data-testid="select-period">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="week">This Week</SelectItem>
                    <SelectItem value="month">This Month</SelectItem>
                    <SelectItem value="quarter">This Quarter</SelectItem>
                    <SelectItem value="year">This Year</SelectItem>
                  </SelectContent>
                </Select>
                <Button variant="outline" data-testid="button-refresh">
                  <RefreshCw className="w-4 h-4 mr-2" />
                  Refresh
                </Button>
                <Button variant="outline" data-testid="button-export">
                  <Download className="w-4 h-4 mr-2" />
                  Export
                </Button>
              </div>
            </div>
          </div>

          {/* Overview Metrics */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Users</CardTitle>
                <Users className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="metric-total-users">
                  {mockReports.overview.totalUsers}
                </div>
                <p className="text-xs text-muted-foreground">
                  {mockReports.overview.userGrowth > 0 ? '+' : ''}{mockReports.overview.userGrowth}% from last period
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Classes</CardTitle>
                <BookOpen className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="metric-total-classes">
                  {mockReports.overview.totalClasses}
                </div>
                <p className="text-xs text-muted-foreground">
                  {mockReports.overview.activeTeachers} active teachers
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Revenue</CardTitle>
                <DollarSign className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="metric-total-revenue">
                  ${mockReports.overview.totalRevenue.toFixed(2)}
                </div>
                <p className="text-xs text-muted-foreground">
                  {mockReports.overview.revenueGrowth > 0 ? '+' : ''}{mockReports.overview.revenueGrowth}% from last period
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Completion Rate</CardTitle>
                <TrendingUp className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold" data-testid="metric-completion-rate">
                  {mockReports.overview.completionRate.toFixed(1)}%
                </div>
                <p className="text-xs text-muted-foreground">Average class completion</p>
              </CardContent>
            </Card>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Top Teachers */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <BarChart3 className="w-5 h-5 mr-2" />
                  Top Performing Teachers
                </CardTitle>
                <CardDescription>Teachers with highest engagement and revenue</CardDescription>
              </CardHeader>
              <CardContent>
                {mockReports.teacherPerformance.length === 0 ? (
                  <p className="text-gray-500 text-center py-8" data-testid="text-no-teacher-data">
                    No teacher performance data available
                  </p>
                ) : (
                  <div className="space-y-4">
                    {mockReports.teacherPerformance.slice(0, 5).map((teacher) => (
                      <div key={teacher.teacherId} className="flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                        <div className="flex-1">
                          <h4 className="font-medium" data-testid={`teacher-name-${teacher.teacherId}`}>
                            {teacher.teacherName}
                          </h4>
                          <div className="flex items-center gap-4 text-sm text-gray-600 dark:text-gray-300">
                            <span data-testid={`teacher-classes-${teacher.teacherId}`}>
                              {teacher.totalClasses} classes
                            </span>
                            <span data-testid={`teacher-students-${teacher.teacherId}`}>
                              {teacher.totalStudents} students
                            </span>
                            <span data-testid={`teacher-rating-${teacher.teacherId}`}>
                              {teacher.averageRating.toFixed(1)}â˜…
                            </span>
                          </div>
                        </div>
                        <div className="text-right">
                          <p className="font-semibold text-green-600" data-testid={`teacher-revenue-${teacher.teacherId}`}>
                            ${teacher.totalRevenue.toFixed(2)}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Popular Classes */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <BookOpen className="w-5 h-5 mr-2" />
                  Most Popular Classes
                </CardTitle>
                <CardDescription>Classes with highest enrollment and completion</CardDescription>
              </CardHeader>
              <CardContent>
                {mockReports.popularClasses.length === 0 ? (
                  <p className="text-gray-500 text-center py-8" data-testid="text-no-class-data">
                    No class performance data available
                  </p>
                ) : (
                  <div className="space-y-4">
                    {mockReports.popularClasses.slice(0, 5).map((classItem) => (
                      <div key={classItem.classId} className="p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                        <div className="flex items-start justify-between mb-2">
                          <h4 className="font-medium" data-testid={`class-title-${classItem.classId}`}>
                            {classItem.title}
                          </h4>
                          <Badge variant="outline" data-testid={`class-rating-${classItem.classId}`}>
                            {classItem.averageRating.toFixed(1)}â˜…
                          </Badge>
                        </div>
                        <div className="flex items-center justify-between text-sm text-gray-600 dark:text-gray-300">
                          <div className="flex items-center gap-4">
                            <span data-testid={`class-enrollments-${classItem.classId}`}>
                              {classItem.enrollmentCount} enrolled
                            </span>
                            <span data-testid={`class-completion-${classItem.classId}`}>
                              {classItem.completionRate.toFixed(1)}% completed
                            </span>
                          </div>
                          <p className="font-semibold text-green-600" data-testid={`class-revenue-${classItem.classId}`}>
                            ${classItem.revenue.toFixed(2)}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Revenue Breakdown */}
          {mockReports.revenueBreakdown.bySubject.length > 0 && (
            <Card className="mt-8">
              <CardHeader>
                <CardTitle className="flex items-center">
                  <DollarSign className="w-5 h-5 mr-2" />
                  Revenue by Subject
                </CardTitle>
                <CardDescription>Revenue distribution across different subjects</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {mockReports.revenueBreakdown.bySubject.map((subject) => (
                    <div key={subject.subject} className="p-4 bg-gray-50 dark:bg-gray-800 rounded-lg">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-medium capitalize" data-testid={`subject-name-${subject.subject}`}>
                          {subject.subject}
                        </h4>
                        <Badge variant="outline" data-testid={`subject-percentage-${subject.subject}`}>
                          {subject.percentage.toFixed(1)}%
                        </Badge>
                      </div>
                      <p className="text-xl font-bold text-green-600" data-testid={`subject-revenue-${subject.subject}`}>
                        ${subject.revenue.toFixed(2)}
                      </p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}

          {/* User Engagement */}
          <Card className="mt-8">
            <CardHeader>
              <CardTitle className="flex items-center">
                <Users className="w-5 h-5 mr-2" />
                User Engagement Metrics
              </CardTitle>
              <CardDescription>Platform usage and engagement statistics</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <div className="text-center">
                  <div className="text-2xl font-bold mb-1" data-testid="engagement-daily-users">
                    {mockReports.userEngagement.dailyActiveUsers}
                  </div>
                  <p className="text-sm text-gray-600 dark:text-gray-300">Daily Active Users</p>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold mb-1" data-testid="engagement-session-duration">
                    {mockReports.userEngagement.averageSessionDuration}m
                  </div>
                  <p className="text-sm text-gray-600 dark:text-gray-300">Avg Session Duration</p>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold mb-1" data-testid="engagement-completion-rate">
                    {mockReports.userEngagement.classCompletionRate.toFixed(1)}%
                  </div>
                  <p className="text-sm text-gray-600 dark:text-gray-300">Class Completion Rate</p>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold mb-1" data-testid="engagement-repeat-rate">
                    {mockReports.userEngagement.repeatEnrollmentRate.toFixed(1)}%
                  </div>
                  <p className="text-sm text-gray-600 dark:text-gray-300">Repeat Enrollment Rate</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>
    </div>
  );
}

client/src/pages/admin-users.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Search, Filter, Mail, Ban, CheckCircle, MoreHorizontal } from "lucide-react";
import { AdminSidebar } from "@/components/admin-sidebar";
import { User } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export function AdminUsers() {
  const [searchQuery, setSearchQuery] = useState("");
  const [roleFilter, setRoleFilter] = useState("");
  const { toast } = useToast();

  const { data: users, isLoading } = useQuery<User[]>({
    queryKey: ["/api/admin/users"],
  });

  const updateUserMutation = useMutation({
    mutationFn: async ({ userId, updates }: { userId: string; updates: Partial<User> }) => {
      return apiRequest("PATCH", `/api/admin/users/${userId}`, updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
      toast({
        title: "Success",
        description: "User updated successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update user",
        variant: "destructive",
      });
    },
  });

  const filteredUsers = users?.filter(user => {
    const matchesSearch = user.firstName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.lastName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.email.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesRole = !roleFilter || user.role === roleFilter;

    return matchesSearch && matchesRole;
  });

  const getRoleColor = (role: string) => {
    switch (role) {
      case 'admin': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300';
      case 'teacher': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300';
      case 'student': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-300';
    }
  };

  const handleRoleChange = (userId: string, newRole: string) => {
    updateUserMutation.mutate({ userId, updates: { role: newRole } });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <AdminSidebar />
        <main className="pl-64 p-8">
          <div className="max-w-7xl mx-auto">
            <div className="text-center">Loading users...</div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <AdminSidebar />
      <main className="pl-64 p-8">
        <div className="max-w-7xl mx-auto">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">User Management</h1>
            <p className="text-gray-600 dark:text-gray-300 mt-2">Manage platform users and their permissions</p>
          </div>

          {/* Filters */}
          <div className="flex gap-4 mb-6">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                placeholder="Search users by name or email..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search"
              />
            </div>
            <Select value={roleFilter} onValueChange={setRoleFilter}>
              <SelectTrigger className="w-40" data-testid="select-role-filter">
                <SelectValue placeholder="All roles" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All roles</SelectItem>
                <SelectItem value="student">Students</SelectItem>
                <SelectItem value="teacher">Teachers</SelectItem>
                <SelectItem value="admin">Admins</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Statistics */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Total Users</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-3xl font-bold" data-testid="stat-total-users">
                  {users?.length || 0}
                </div>
              </CardContent>
            </Card>
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Teachers</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-3xl font-bold" data-testid="stat-teachers">
                  {users?.filter(u => u.role === 'teacher').length || 0}
                </div>
              </CardContent>
            </Card>
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Students</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-3xl font-bold" data-testid="stat-students">
                  {users?.filter(u => u.role === 'student').length || 0}
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Users Table */}
          {!filteredUsers || filteredUsers.length === 0 ? (
            <Card>
              <CardContent className="text-center py-12">
                <h3 className="text-xl font-semibold mb-2" data-testid="text-no-users">
                  {searchQuery || roleFilter ? "No users found" : "No users yet"}
                </h3>
                <p className="text-gray-600 dark:text-gray-300">
                  {searchQuery || roleFilter 
                    ? "Try adjusting your search or filters"
                    : "Users will appear here as they sign up"
                  }
                </p>
              </CardContent>
            </Card>
          ) : (
            <Card>
              <CardHeader>
                <CardTitle>Users ({filteredUsers.length})</CardTitle>
                <CardDescription>Manage user accounts and permissions</CardDescription>
              </CardHeader>
              <CardContent className="p-0">
                <div className="space-y-0 divide-y">
                  {filteredUsers.map((user) => (
                    <div key={user.id} className="p-6 flex items-center justify-between">
                      <div className="flex items-center gap-4 flex-1">
                        <Avatar className="h-10 w-10">
                          <AvatarFallback data-testid={`avatar-${user.id}`}>
                            {user.firstName[0]}{user.lastName[0]}
                          </AvatarFallback>
                        </Avatar>

                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <h3 className="font-semibold" data-testid={`text-user-name-${user.id}`}>
                              {user.firstName} {user.lastName}
                            </h3>
                            <Badge className={getRoleColor(user.role)} data-testid={`badge-role-${user.id}`}>
                              {user.role.charAt(0).toUpperCase() + user.role.slice(1)}
                            </Badge>
                          </div>
                          <p className="text-sm text-gray-600 dark:text-gray-300" data-testid={`text-user-email-${user.id}`}>
                            {user.email}
                          </p>
                          <p className="text-xs text-gray-500 dark:text-gray-400" data-testid={`text-user-date-${user.id}`}>
                            Joined {new Date(user.createdAt!).toLocaleDateString()}
                          </p>
                        </div>
                      </div>

                      <div className="flex items-center gap-2">
                        <Select 
                          value={user.role} 
                          onValueChange={(newRole) => handleRoleChange(user.id, newRole)}
                        >
                          <SelectTrigger className="w-32" data-testid={`select-user-role-${user.id}`}>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="student">Student</SelectItem>
                            <SelectItem value="teacher">Teacher</SelectItem>
                            <SelectItem value="admin">Admin</SelectItem>
                          </SelectContent>
                        </Select>

                        <Button variant="ghost" size="sm" data-testid={`button-email-${user.id}`}>
                          <Mail className="w-4 h-4" />
                        </Button>

                        <Button variant="ghost" size="sm" data-testid={`button-more-${user.id}`}>
                          <MoreHorizontal className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      </main>
    </div>
  );
}

client/src/pages/student-browse.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Search, Filter, CalendarDays, Clock, Users, Star } from "lucide-react";
import { StudentSidebar } from "@/components/student-sidebar";
import { Class, User } from "@shared/schema";

interface ClassWithTeacher extends Class {
  teacher: User;
}

export function StudentBrowse() {
  const [searchQuery, setSearchQuery] = useState("");
  const [subjectFilter, setSubjectFilter] = useState("");

  const { data: classes, isLoading } = useQuery<ClassWithTeacher[]>({
    queryKey: ["/api/classes/browse"],
  });

  const filteredClasses = classes?.filter(classItem => {
    const matchesSearch = classItem.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      classItem.description?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      classItem.teacher.firstName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      classItem.teacher.lastName.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesSubject = !subjectFilter || classItem.subject === subjectFilter;

    return matchesSearch && matchesSubject;
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <StudentSidebar />
        <main className="pl-64 p-8">
          <div className="max-w-7xl mx-auto">
            <div className="text-center">Loading classes...</div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <StudentSidebar />
      <main className="pl-64 p-8">
        <div className="max-w-7xl mx-auto">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Browse Classes</h1>
            <p className="text-gray-600 dark:text-gray-300 mt-2">Discover and enroll in live classes</p>
          </div>

          {/* Filters */}
          <div className="flex gap-4 mb-6">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                placeholder="Search classes, teachers, or topics..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search"
              />
            </div>
            <Select value={subjectFilter} onValueChange={setSubjectFilter}>
              <SelectTrigger className="w-48" data-testid="select-subject-filter">
                <SelectValue placeholder="All subjects" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="">All subjects</SelectItem>
                <SelectItem value="programming">Programming</SelectItem>
                <SelectItem value="design">Design</SelectItem>
                <SelectItem value="business">Business</SelectItem>
                <SelectItem value="marketing">Marketing</SelectItem>
                <SelectItem value="data-science">Data Science</SelectItem>
                <SelectItem value="other">Other</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {!filteredClasses || filteredClasses.length === 0 ? (
            <Card>
              <CardContent className="text-center py-12">
                <h3 className="text-xl font-semibold mb-2" data-testid="text-no-classes">
                  {searchQuery || subjectFilter ? "No classes found" : "No classes available"}
                </h3>
                <p className="text-gray-600 dark:text-gray-300">
                  {searchQuery || subjectFilter 
                    ? "Try adjusting your search or filters"
                    : "Check back later for new classes"
                  }
                </p>
              </CardContent>
            </Card>
          ) : (
            <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
              {filteredClasses?.map((classItem) => (
                <Card key={classItem.id} className="hover:shadow-lg transition-shadow">
                  <CardHeader>
                    <div className="flex items-start justify-between mb-2">
                      <Badge variant="secondary" data-testid={`badge-subject-${classItem.id}`}>
                        {classItem.subject}
                      </Badge>
                      {classItem.isLive && (
                        <Badge className="bg-red-500 hover:bg-red-600" data-testid={`badge-live-${classItem.id}`}>
                          LIVE
                        </Badge>
                      )}
                    </div>
                    <CardTitle className="text-lg mb-2" data-testid={`text-class-title-${classItem.id}`}>
                      {classItem.title}
                    </CardTitle>
                    <CardDescription className="line-clamp-2 mb-4" data-testid={`text-class-description-${classItem.id}`}>
                      {classItem.description}
                    </CardDescription>
                  </CardHeader>
                  <CardContent>
                    {/* Teacher Info */}
                    <div className="flex items-center gap-3 mb-4">
                      <Avatar className="h-8 w-8">
                        <AvatarFallback data-testid={`avatar-teacher-${classItem.id}`}>
                          {classItem.teacher.firstName[0]}{classItem.teacher.lastName[0]}
                        </AvatarFallback>
                      </Avatar>
                      <div>
                        <p className="text-sm font-medium" data-testid={`text-teacher-name-${classItem.id}`}>
                          {classItem.teacher.firstName} {classItem.teacher.lastName}
                        </p>
                      </div>
                    </div>

                    {/* Class Details */}
                    <div className="space-y-2 mb-4">
                      <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                        <CalendarDays className="w-4 h-4 mr-2" />
                        <span data-testid={`text-class-date-${classItem.id}`}>
                          {new Date(classItem.date).toLocaleDateString()}
                        </span>
                      </div>
                      <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                        <Clock className="w-4 h-4 mr-2" />
                        <span data-testid={`text-class-duration-${classItem.id}`}>
                          {classItem.duration} minutes
                        </span>
                      </div>
                      <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                        <Users className="w-4 h-4 mr-2" />
                        <span data-testid={`text-class-capacity-${classItem.id}`}>
                          {classItem.maxStudents} max students
                        </span>
                      </div>
                    </div>

                    {/* Pricing and Enrollment */}
                    <div className="flex items-center justify-between">
                      <span className="text-2xl font-bold text-green-600" data-testid={`text-class-price-${classItem.id}`}>
                        ${classItem.price}
                      </span>
                      <Button data-testid={`button-enroll-${classItem.id}`}>
                        Enroll Now
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

client/src/pages/student-library.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { FileText, Video, Image, Search, Download, BookOpen, Play } from "lucide-react";
import { StudentSidebar } from "@/components/student-sidebar";
import { Content } from "@shared/schema";

interface ContentWithClass extends Content {
  className: string;
}

export function StudentLibrary() {
  const [searchQuery, setSearchQuery] = useState("");

  const { data: content, isLoading } = useQuery<ContentWithClass[]>({
    queryKey: ["/api/student/library"],
  });

  const filteredContent = content?.filter(item =>
    item.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    item.className.toLowerCase().includes(searchQuery.toLowerCase()) ||
    item.content?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const videoContent = filteredContent?.filter(item => item.type === 'video');
  const pdfContent = filteredContent?.filter(item => item.type === 'pdf');
  const slideContent = filteredContent?.filter(item => item.type === 'slide');
  const noteContent = filteredContent?.filter(item => item.type === 'note');

  const getContentIcon = (type: string) => {
    switch (type) {
      case "video": return <Video className="w-5 h-5" />;
      case "pdf": return <FileText className="w-5 h-5" />;
      case "slide": return <Image className="w-5 h-5" />;
      default: return <FileText className="w-5 h-5" />;
    }
  };

  const getContentTypeColor = (type: string) => {
    switch (type) {
      case "video": return "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300";
      case "pdf": return "bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300";
      case "slide": return "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300";
      default: return "bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-300";
    }
  };

  const renderContentGrid = (items: ContentWithClass[] | undefined, emptyMessage: string) => {
    if (!items || items.length === 0) {
      return (
        <div className="text-center py-12">
          <p className="text-gray-500" data-testid="text-no-content">{emptyMessage}</p>
        </div>
      );
    }

    return (
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {items.map((item) => (
          <Card key={item.id} className="hover:shadow-md transition-shadow">
            <CardHeader>
              <div className="flex items-start justify-between">
                <div className={`p-2 rounded-lg ${getContentTypeColor(item.type)}`}>
                  {getContentIcon(item.type)}
                </div>
                <Badge variant="outline" data-testid={`badge-type-${item.id}`}>
                  {item.type.toUpperCase()}
                </Badge>
              </div>
              <CardTitle className="text-lg" data-testid={`text-content-title-${item.id}`}>
                {item.title}
              </CardTitle>
              <CardDescription data-testid={`text-class-name-${item.id}`}>
                From: {item.className}
              </CardDescription>
            </CardHeader>
            <CardContent>
              {item.content && (
                <p className="text-sm text-gray-600 dark:text-gray-300 mb-4 line-clamp-3" data-testid={`text-content-description-${item.id}`}>
                  {item.content}
                </p>
              )}

              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-500 dark:text-gray-400" data-testid={`text-content-date-${item.id}`}>
                  {new Date(item.createdAt!).toLocaleDateString()}
                </span>

                <div className="flex gap-2">
                  {item.type === 'video' ? (
                    <Button size="sm" data-testid={`button-play-${item.id}`}>
                      <Play className="w-4 h-4 mr-2" />
                      Play
                    </Button>
                  ) : (
                    <Button size="sm" data-testid={`button-view-${item.id}`}>
                      <BookOpen className="w-4 h-4 mr-2" />
                      View
                    </Button>
                  )}
                  <Button variant="ghost" size="sm" data-testid={`button-download-${item.id}`}>
                    <Download className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
        <StudentSidebar />
        <main className="pl-64 p-8">
          <div className="max-w-7xl mx-auto">
            <div className="text-center">Loading your library...</div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <StudentSidebar />
      <main className="pl-64 p-8">
        <div className="max-w-7xl mx-auto">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Content Library</h1>
            <p className="text-gray-600 dark:text-gray-300 mt-2">Access materials from your enrolled classes</p>
          </div>

          <div className="mb-6">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input
                placeholder="Search content, classes, or topics..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search"
              />
            </div>
          </div>

          {!content || content.length === 0 ? (
            <Card>
              <CardContent className="text-center py-12">
                <BookOpen className="w-16 h-16 mx-auto text-gray-400 mb-4" />
                <h3 className="text-xl font-semibold mb-2" data-testid="text-no-library">No content available</h3>
                <p className="text-gray-600 dark:text-gray-300 mb-6">
                  Content from your enrolled classes will appear here
                </p>
              </CardContent>
            </Card>
          ) : (
            <Tabs defaultValue="all" className="space-y-6">
              <TabsList className="grid w-full grid-cols-5">
                <TabsTrigger value="all" data-testid="tab-all">
                  All ({filteredContent?.length || 0})
                </TabsTrigger>
                <TabsTrigger value="videos" data-testid="tab-videos">
                  Videos ({videoContent?.length || 0})
                </TabsTrigger>
                <TabsTrigger value="pdfs" data-testid="tab-pdfs">
                  PDFs ({pdfContent?.length || 0})
                </TabsTrigger>
                <TabsTrigger value="slides" data-testid="tab-slides">
                  Slides ({slideContent?.length || 0})
                </TabsTrigger>
                <TabsTrigger value="notes" data-testid="tab-notes">
                  Notes ({noteContent?.length || 0})
                </TabsTrigger>
              </TabsList>

              <TabsContent value="all">
                {renderContentGrid(filteredContent, 
                  searchQuery ? "No content found matching your search" : "No content available"
                )}
              </TabsContent>

              <TabsContent value="videos">
                {renderContentGrid(videoContent, "No video content available")}
              </TabsContent>

              <TabsContent value="pdfs">
                {renderContentGrid(pdfContent, "No PDF content available")}
              </TabsContent>

              <TabsContent value="slides">
                {renderContentGrid(slideContent, "No slide content available")}
              </TabsContent>

              <TabsContent value="notes">
                {renderContentGrid(noteContent, "No notes available")}
              </TabsContent>
            </Tabs>
          )}
        </div>
      </main>
    </div>
  );
}

